import React, { useEffect, useState } from 'react';
import './'; // nëse ke stilizim
import axios from 'axios';

const Consumers = () => {
  const [consumers, setConsumers] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/consumers')
      .then(response => {
        setConsumers(response.data);
      })
      .catch(error => {
        console.error('Gabim gjatë marrjes së konsumatorëve:', error);
      });
  }, []);

  return (
    <div className="consumers-page">
      <h2>Lista e Konsumatorëve</h2>
      <ul>
        {consumers.map(consumer => (
          <li key={consumer.id}>
            <strong>{consumer.name}</strong> – {consumer.type} – {consumer.address}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Consumers;
