import React, { useEffect, useState } from 'react';
import '../styles/consumers.css';

const Consumers = () => {
  const [consumers, setConsumers] = useState([]);
  const [form, setForm] = useState({ name: '', address: '', type: 'Individual' });

  useEffect(() => {
    fetch('http://localhost:5000/api/consumers')
      .then(res => res.json())
      .then(data => setConsumers(data))
      .catch(err => console.error('Gabim gjatë marrjes së konsumatorëve:', err));
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/api/consumers', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });
    const data = await res.json();
    setConsumers([...consumers, data]);
    setForm({ name: '', address: '', type: 'Individual' }); // reseto formularin
  };

  return (
    <div className="consumers-container">
      <h2>Lista e Konsumatorëve</h2>

      <form onSubmit={handleSubmit} className="form-container">
        <input
          type="text"
          name="name"
          placeholder="Emri"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Adresa"
          value={form.address}
          onChange={handleChange}
          required
        />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="Individual">Individual</option>
          <option value="Biznes">Biznes</option>
        </select>
        <button type="submit">Shto Konsumatorin</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Emri</th>
            <th>Adresa</th>
            <th>Tipi</th>
          </tr>
        </thead>
        <tbody>
          {consumers.map(consumer => (
            <tr key={consumer.id}>
              <td>{consumer.name}</td>
              <td>{consumer.address}</td>
              <td>{consumer.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Consumers;
