import React, { useEffect, useState } from 'react';
import '../styles/consumers.css';

const Consumers = () => {
  const [consumers, setConsumers] = useState([]);
  const [form, setForm] = useState({ name: '', address: '', type: 'Individual' });

  useEffect(() => {
    fetch('http://localhost:5000/api/consumers')
      .then(res => res.json())
      .then(data => setConsumers(data))
      .catch(err => console.error('Gabim gjatë marrjes së konsumatorëve:', err));
  }, []);

const [newConsumer, setNewConsumer] = useState({
  name: '',
  address: '',
  type: '',
});

const handleChange = (e) => {
  setNewConsumer({ ...newConsumer, [e.target.name]: e.target.value });
};

const handleSubmit = (e) => {
  e.preventDefault();
  axios.post('http://localhost:5000/api/consumers', {
    name,
    address,
    type,
  })
    .then(res => {
      setConsumers([...consumers, res.data]);
      setName('');
      setAddress('');
      setType('');
    })
    .catch(err => {
      console.error('Gabim gjatë shtimit të konsumatorit:', err);
    });
};


  return (
    <div className="consumers-container">
      <h2>Lista e Konsumatorëve</h2>

      <form onSubmit={handleSubmit} className="form-container">
        <input
          type="text"
          name="name"
          placeholder="Emri"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Adresa"
          value={form.address}
          onChange={handleChange}
          required
        />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="Individual">Individual</option>
          <option value="Biznes">Biznes</option>
        </select>
        <button type="submit">Shto Konsumatorin</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Emri</th>
            <th>Adresa</th>
            <th>Tipi</th>
          </tr>
        </thead>
        <tbody>
          {consumers.map(consumer => (
            <tr key={consumer.id}>
              <td>{consumer.name}</td>
              <td>{consumer.address}</td>
              <td>{consumer.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
    </div>
  );
};

export default Consumers;
