import { useState } from "react";
imp

export default function Consumers() {
  const [consumers, setConsumers] = useState([]);
  const [form, setForm] = useState({
    name: "",
    surname: "",
    address: "",
    meter: "",
    type: "amviseri",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const addConsumer = (e) => {
    e.preventDefault();
    const newConsumer = { ...form, id: Date.now() };
    setConsumers([...consumers, newConsumer]);
    setForm({
      name: "",
      surname: "",
      address: "",
      meter: "",
      type: "amviseri",
    });
  };

  const deleteConsumer = (id) => {
    setConsumers(consumers.filter((c) => c.id !== id));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Shto Konsumator</h2>
      <form onSubmit={addConsumer}>
        <input
          type="text"
          name="name"
          placeholder="Emri"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="surname"
          placeholder="Mbiemri"
          value={form.surname}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Adresa"
          value={form.address}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="meter"
          placeholder="Numri i matësit"
          value={form.meter}
          onChange={handleChange}
          required
        />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="amviseri">Amvisëri</option>
          <option value="biznes">Biznes</option>
        </select>
        <button type="submit">Shto</button>
      </form>

      <h3 style={{ marginTop: "30px" }}>Lista e konsumatorëve</h3>
      <ul>
        {consumers.map((c) => (
          <li key={c.id}>
            {c.name} {c.surname} | {c.address} | {c.meter} | {c.type}{" "}
            <button onClick={() => deleteConsumer(c.id)}>Fshij</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
