import React, { useEffect, useState } from 'react';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [consumers, setConsumers] = useState([]);

  
useEffect(() => {
  fetch('http://localhost:5000/api/consumers')
    .then(res => res.json())
    .then(data => {
      console.log('consumers from backend:', data);
      if (Array.isArray(data)) {
        setConsumers(data);
      } else {
        console.error('Data nuk është array:', data);
        setConsumers([]); // ose bëj diçka tjetër
      }
    })
    .catch(err => {
      console.error('Gabim fetch:', err);
      setConsumers([]);
    });
}, []);

  const total = consumers.length;
  const biznes = consumers.filter(c => c.type === 'Biznes').length;
  const individual = consumers.filter(c => c.type === 'Individual').length;

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      <div className="cards">
        <div className="card">
          <h3>Totali i Konsumatorëve</h3>
          <p>{total}</p>
        </div>
        <div className="card">
          <h3>Biznes</h3>
          <p>{biznes}</p>
        </div>
        <div className="card">
          <h3>Amvisëri</h3>
          <p>{individual}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
