import { useState, useEffect } from "react";
import "../styles/consumers.css"; // Mund ta përdorësh edhe History.css nëse janë identike

export default function Consumers() {
  const [consumers, setConsumers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/consumers")
      .then((res) => res.json())
      .then((data) => setConsumers(data))
      .catch((err) => console.error("Gabim në marrjen e konsumatorëve:", err));
  }, []);

  return (
    <div className="history-container">
      <h2>Lista e Konsumatorëve</h2>

      <div className="history-table-container">
        {consumers.length > 0 ? (
          <table className="history-table">
            <thead>
              <tr>
                <th>Emri</th>
                <th>Mbiemri</th>
                <th>Adresa</th>
                <th>Nr. Faturës</th>
              </tr>
            </thead>
            <tbody>
              {consumers.map((c) => (
                <tr key={c.id}>
                  <td data-label="Emri">{c.name}</td>
                  <td data-label="Mbiemri">{c.surname}</td>
                  <td data-label="Adresa">{c.address}</td>
                  <td data-label="Fatura">{c.invoiceNumber}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nuk ka të dhëna për konsumatorë.</p>
        )}
      </div>
    </div>
  );
}
