import { useState } from "react";
import '../styles/History.css';

export default function History() {
  const [consumers] = useState([
    { id: 1, name: "Arben", surname: "Bashkimi", meter: "10001", type: "amviseri" },
    { id: 2, name: "Teuta", surname: "Bujari", meter: "10002", type: "biznes" },
  ]);

  const [readings] = useState([
    { id: 101, consumerId: 1, previousReading: 120, currentReading: 140, consumption: 20, total: 10, date: new Date("2024-04-01") },
    { id: 102, consumerId: 1, previousReading: 140, currentReading: 160, consumption: 20, total: 10, date: new Date("2024-05-01") },
    { id: 103, consumerId: 2, previousReading: 500, currentReading: 560, consumption: 60, total: 72, date: new Date("2024-05-01") },
  ]);

  const [selectedId, setSelectedId] = useState("");

  const filteredReadings = readings
    .filter(r => r.consumerId === parseInt(selectedId))
    .sort((a, b) => b.date - a.date);

  const selectedConsumer = consumers.find(c => c.id === parseInt(selectedId));

  return (
    <div style={{ padding: "20px" }}>
      <h2>Historiku i leximeve</h2>
      
      <select value={selectedId} onChange={e => setSelectedId(e.target.value)}>
        <option value="">Zgjedh konsumatorin</option>
        {consumers.map(c => (
          <option key={c.id} value={c.id}>
            {c.name} {c.surname}
          </option>
        ))}
      </select>

      {!selectedId && <p style={{ marginTop: '15px' }}>Zgjidhni një konsumator për të parë historikun.</p>}

      {selectedId && (
        <div style={{ marginTop: "20px" }}>
          <h3>Lexime për: {selectedConsumer?.name} {selectedConsumer?.surname}</h3>
          {filteredReadings.length > 0 ? (
            <table border="1" cellPadding="5" style={{ marginTop: "10px" }}>
              <thead>
                <tr>
                  <th>Data</th>
                  <th>Leximi mëparshëm</th>
                  <th>Leximi aktual</th>
                  <th>Konsumi</th>
                  <th>Pagesa (Den)</th>
                </tr>
              </thead>
              <tbody>
                {filteredReadings.map(r => (
                  <tr key={r.id}>
                    <td>{r.date.toLocaleDateString('sq-AL', { year: 'numeric', month: 'long', day: 'numeric' })}</td>
                    <td>{r.previousReading}</td>
                    <td>{r.currentReading}</td>
                    <td>{r.consumption} m³</td>
                    <td>{r.total.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Nuk ka lexime për këtë konsumator.</p>
          )}
        </div>
      )}
    </div>
  );
}
