import { useState, useEffect } from "react";
import '../styles/History.css';

export default function History() {
  const [consumers, setConsumers] = useState([]);
  const [readings, setReadings] = useState([]);
  const [selectedId, setSelectedId] = useState("");

  // Merr konsumatorët nga backend kur komponenti ngarkohet
  useEffect(() => {
    fetch('http://localhost:5000/api/consumers')
      .then(res => res.json())
      .then(data => setConsumers(data))
      .catch(err => console.error('Gabim gjatë marrjes së konsumatorëve:', err));
  }, []);

  // Merr leximet e konsumatorit të zgjedhur sa herë që ndryshon selectedId
  useEffect(() => {
    if (!selectedId) {
      setReadings([]);
      return;
    }
    fetch(`http://localhost:5000/api/readings/${selectedId}`)
      .then(res => res.json())
      .then(data => {
        // Si në backend, data.date është string, e konvertojmë në Date objekt
        const readingsWithDates = data.map(r => ({ ...r, date: new Date(r.date) }));
        setReadings(readingsWithDates);
      })
      .catch(err => console.error('Gabim gjatë marrjes së leximeve:', err));
  }, [selectedId]);

  const selectedConsumer = consumers.find(c => c.id === parseInt(selectedId));

  return (
    <div style={{ padding: "20px" }}>
      <h2>Historiku i leximeve</h2>
      <select
        value={selectedId}
        onChange={(e) => setSelectedId(e.target.value)}
      >
        <option value="">Zgjedh konsumatorin</option>
        {consumers.map((c) => (
          <option key={c.id} value={c.id}>
            {c.name} {c.surname}
          </option>
        ))}
      </select>

      {selectedId && selectedConsumer && (
        <div style={{ marginTop: "20px" }}>
          <h3>Lexime për: {selectedConsumer.name} {selectedConsumer.surname}</h3>
          {readings.length > 0 ? (
            <table border="1" cellPadding="5" style={{ marginTop: "10px" }}>
              <thead>
                <tr>
                  <th>Data</th>
                  <th>Leximi mëparshëm</th>
                  <th>Leximi aktual</th>
                  <th>Konsumi</th>
                  <th>Pagesa (€)</th>
                </tr>
              </thead>
              <tbody>
                {readings.map((r) => (
                  <tr key={r.id}>
                    <td>{r.date.toLocaleDateString()}</td>
                    <td>{r.previousReading}</td>
                    <td>{r.currentReading}</td>
                    <td>{r.consumption} m³</td>
                    <td>{r.total.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Nuk ka lexime për këtë konsumator.</p>
          )}
        </div>
      )}
    </div>
  );
}
