import { useEffect, useState, useMemo } from "react";
import jsPDF from 'jspdf';
import "jspdf-autotable";
import '../styles/readings.css';

export default function Readings() {
  const [consumers, setConsumers] = useState([]);
  const [loadingConsumers, setLoadingConsumers] = useState(true);

  const [readings, setReadings] = useState(() => {
    const saved = localStorage.getItem("readings");
    return saved ? JSON.parse(saved) : [];
  });

  const [form, setForm] = useState({
    consumerId: "",
    currentReading: "",
  });

  const [submitting, setSubmitting] = useState(false);

  // Filtrat
  const [searchTerm, setSearchTerm] = useState("");
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/consumers")
      .then((res) => res.json())
      .then((data) => {
        setConsumers(data);
        setLoadingConsumers(false);
      })
      .catch((err) => {
        console.error("Gabim gjatë marrjes së konsumatorëve:", err);
        setLoadingConsumers(false);
      });
  }, []);

  useEffect(() => {
    localStorage.setItem("readings", JSON.stringify(readings));
  }, [readings]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitReading = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    const consumerIdNum = parseInt(form.consumerId);
    const consumer = consumers.find((c) => c.id === consumerIdNum);
    if (!consumer) {
      alert("Zgjidh një konsumator të vlefshëm");
      setSubmitting(false);
      return;
    }

    const current = parseFloat(form.currentReading);
    if (isNaN(current) || current < 0) {
      alert("Fut një lexim të vlefshëm numerik");
      setSubmitting(false);
      return;
    }

    const lastReading = readings
      .filter((r) => r.consumerId === consumerIdNum)
      .sort((a, b) => new Date(b.date) - new Date(a.date))[0];

    const previous = lastReading ? lastReading.currentReading : 0;

    if (current < previous) {
      alert(
        `Leximi i tanishëm (${current}) nuk mund të jetë më i vogël se leximi i mëparshëm (${previous})`
      );
      setSubmitting(false);
      return;
    }

    const pricePerM3 = consumer.type.toLowerCase() === "biznes" ? 1.2 : 0.5;
    const consumption = current - previous;
    const total = consumption * pricePerM3;

    const newReading = {
      id: Date.now(), // gjeneroj një id unike për shembull
      consumerId: consumer.id,
      name: consumer.name,
      surname: consumer.surname,
      address: consumer.address,
      type: consumer.type,
      previousReading: previous,
      currentReading: current,
      consumption,
      total,
      date: new Date().toISOString(),
    };

    try {
      const response = await fetch("http://localhost:5000/api/readings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newReading),
      });

      if (!response.ok) {
        throw new Error("Gabim gjatë ruajtjes së leximit në backend");
      }

      const savedReading = await response.json();

      setReadings((prev) => [...prev, savedReading]);
      setForm({ consumerId: "", currentReading: "" });
    } catch (error) {
      alert(error.message);
    } finally {
      setSubmitting(false);
    }
  };

  const generatePDF = (reading, consumer) => {
    if (!consumer) {
      alert("Konsumatori nuk u gjet për këtë lexim.");
      return;
    }
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Fatura për Ujë", 70, 20);

    doc.setFontSize(12);
    doc.text(`Emri: ${consumer.name} ${consumer.surname}`, 20, 40);
    doc.text(`Adresa: ${consumer.address}`, 20, 50);
    doc.text(`Tipi: ${consumer.type}`, 20, 60);
    doc.text(`Data: ${new Date(reading.date).toLocaleDateString()}`, 20, 70);

    doc.autoTable({
      startY: 80,
      head: [["Leximi i Mëparshëm", "Leximi i Tanishëm", "Konsumi (m³)", "Çmimi për m³", "Totali"]],
      body: [
        [
          reading.previousReading,
          reading.currentReading,
          reading.consumption,
          consumer.type.toLowerCase() === "biznes" ? "1.2" : "0.5",
          `${reading.total.toFixed(2)} €`,
        ],
      ],
    });

    doc.save(`fature_${consumer.name}_${consumer.surname}.pdf`);
  };

  const filteredReadings = useMemo(() => {
    return readings.filter(r => {
      const consumerName = `${r.name} ${r.surname}`.toLowerCase();
      const searchLower = searchTerm.toLowerCase();

      const matchesName = consumerName.includes(searchLower);

      const readingDate = new Date(r.date);
      const fromDate = dateFrom ? new Date(dateFrom) : null;
      const toDate = dateTo ? new Date(dateTo) : null;

      const matchesDate =
        (!fromDate || readingDate >= fromDate) &&
        (!toDate || readingDate <= toDate);

      return matchesName && matchesDate;
    });
  }, [readings, searchTerm, dateFrom, dateTo]);

  return (
    <div className="readings-container">
      <h2>Lexim i ri i ujit</h2>
      <form onSubmit={submitReading} className="readings-form">
        <select
          name="consumerId"
          value={form.consumerId}
          onChange={handleChange}
          required
          disabled={loadingConsumers || submitting}
        >
          <option value="">Zgjedh konsumatorin</option>
          {consumers.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name} {c.surname} - {c.type}
            </option>
          ))}
        </select>
        <input
          type="number"
          name="currentReading"
          placeholder="Leximi i tanishëm"
          value={form.currentReading}
          onChange={handleChange}
          required
          min="0"
          step="any"
          disabled={submitting}
        />
        <button type="submit" disabled={submitting}>
          {submitting ? "Po ruhet..." : "Ruaj leximin"}
        </button>
      </form>

      <div className="filters">
        <input
          type="text"
          placeholder="Kërko sipas emrit të konsumatorit"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <input
          type="date"
          value={dateFrom}
          onChange={(e) => setDateFrom(e.target.value)}
        />
        <input
          type="date"
          value={dateTo}
          onChange={(e) => setDateTo(e.target.value)}
        />
      </div>

      <h3>Historiku i leximeve</h3>

      {filteredReadings.length === 0 ? (
        <p className="no-readings-msg">Nuk ka lexime të regjistruara që plotësojnë kriteret.</p>
      ) : (
        <div className="readings-table-wrapper">
          <table className="readings-table">
            <thead>
              <tr>
                <th>Konsumatori</th>
                <th style={{ textAlign: "right" }}>Leximi i mëparshëm</th>
                <th style={{ textAlign: "right" }}>Leximi i tanishëm</th>
                <th style={{ textAlign: "right" }}>Konsumi (m³)</th>
                <th style={{ textAlign: "right" }}>Totali (€)</th>
                <th style={{ textAlign: "right" }}>Data</th>
                <th>PDF</th>
              </tr>
            </thead>
            <tbody>
              {filteredReadings
                .sort((a, b) => new Date(b.date) - new Date(a.date))
                .map((r) => (
                  <tr key={r.id}>
                    <td>{r.name} {r.surname}</td>
                    <td className="numeric">{r.previousReading}</td>
                    <td className="numeric">{r.currentReading}</td>
                    <td className="numeric">{r.consumption}</td>
                    <td className="numeric">€{r.total.toFixed(2)}</td>
                    <td className="numeric">{new Date(r.date).toLocaleString()}</td>
                    <td>
                      <button onClick={() => generatePDF(r, consumers.find(c => c.id === r.consumerId))}>
                        Shkarko PDF
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
