import { useEffect, useState } from "react";

export default function Readings() {
  const [consumers, setConsumers] = useState([]);
  const [readings, setReadings] = useState(() => {
    // Ngarko leximet nga localStorage në fillim
    const saved = localStorage.getItem("readings");
    return saved ? JSON.parse(saved) : [];
  });

  const [form, setForm] = useState({
    consumerId: "",
    currentReading: "",
  });

  useEffect(() => {
    // Merr konsumatorët nga backend (ndrysho URL sipas backend-it tënd)
    fetch("http://localhost:5000/api/consumers")
      .then((res) => res.json())
      .then((data) => setConsumers(data))
      .catch((err) => console.error("Gabim gjatë marrjes së konsumatorëve:", err));
  }, []);

  useEffect(() => {
    // Ruaj leximet në localStorage sa herë ndryshojnë
    localStorage.setItem("readings", JSON.stringify(readings));
  }, [readings]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitReading = (e) => {
    e.preventDefault();

    const consumerIdNum = parseInt(form.consumerId);
    const consumer = consumers.find((c) => c.id === consumerIdNum);
    if (!consumer) {
      alert("Zgjidh një konsumator të vlefshëm");
      return;
    }

    const current = parseFloat(form.currentReading);
    if (isNaN(current) || current < 0) {
      alert("Fut një lexim të vlefshëm numerik");
      return;
    }

    // Gjej leximin e fundit të këtij konsumatori
    const lastReading = readings
      .filter((r) => r.consumerId === consumerIdNum)
      .sort((a, b) => new Date(b.date) - new Date(a.date))[0];

    const previous = lastReading ? lastReading.currentReading : 0;

    if (current < previous) {
      alert(`Leximi i tanishëm (${current}) nuk mund të jetë më i vogël se leximi i mëparshëm (${previous})`);
      return;
    }

    const consumption = current - previous;
    const pricePerM3 = consumer.type === "biznes" ? 1.2 : 0.5;
    const total = consumption * pricePerM3;

    const newReading = {
      id: Date.now(),
      consumerId: consumer.id,
      name: consumer.name,
      surname: consumer.surname,
      previousReading: previous,
      currentReading: current,
      consumption,
      total,
      date: new Date().toISOString(),
    };

    setReadings([...readings, newReading]);
    setForm({ consumerId: "", currentReading: "" });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Lexim i ri i ujit</h2>
      <form onSubmit={submitReading}>
        <select
          name="consumerId"
          value={form.consumerId}
          onChange={handleChange}
          required
        >
          <option value="">Zgjedh konsumatorin</option>
          {consumers.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name} {c.surname} - {c.type}
            </option>
          ))}
        </select>
        <input
          type="number"
          name="currentReading"
          placeholder="Leximi i tanishëm"
          value={form.currentReading}
          onChange={handleChange}
          required
          min="0"
          step="any"
        />
        <button type="submit">Ruaj leximin</button>
      </form>

      <h3 style={{ marginTop: "30px" }}>Historiku i leximeve</h3>
      <ul>
        {readings
          .sort((a, b) => new Date(b.date) - new Date(a.date))
          .map((r) => (
            <li key={r.id}>
              {r.name} {r.surname} | {r.previousReading} → {r.currentReading} m³ | Konsumi: {r.consumption} m³ | Totali: €{r.total.toFixed(2)} |{" "}
              {new Date(r.date).toLocaleString()}
            </li>
          ))}
      </ul>
    </div>
  );
}
