import { useState } from "react";

export default function Readings() {
  const [consumers, setConsumers] = useState([
    {
      id: 1,
      name: "Arben",
      surname: "Bashkimi",
      meter: "10001",
      type: "amviseri",
    },
    {
      id: 2,
      name: "Teuta",
      surname: "Bujari",
      meter: "10002",
      type: "biznes",
    },
  ]);

  const [readings, setReadings] = useState([]);

  const [form, setForm] = useState({
    consumerId: "",
    currentReading: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const submitReading = (e) => {
    e.preventDefault();
    const consumer = consumers.find((c) => c.id === parseInt(form.consumerId));
    const lastReading = readings
      .filter((r) => r.consumerId === parseInt(form.consumerId))
      .sort((a, b) => b.date - a.date)[0];

    const previous = lastReading ? lastReading.currentReading : 0;
    const current = parseFloat(form.currentReading);
    const consumption = current - previous;

    const pricePerM3 = consumer.type === "biznes" ? 1.2 : 0.5;
    const total = consumption * pricePerM3;

    const newReading = {
      id: Date.now(),
      consumerId: consumer.id,
      name: consumer.name,
      surname: consumer.surname,
      previousReading: previous,
      currentReading: current,
      consumption,
      total,
      date: new Date(),
    };

    setReadings([...readings, newReading]);
    setForm({ consumerId: "", currentReading: "" });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Lexim i ri i ujit</h2>
      <form onSubmit={submitReading}>
        <select
          name="consumerId"
          value={form.consumerId}
          onChange={handleChange}
          required
        >
          <option value="">Zgjedh konsumatorin</option>
          {consumers.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name} {c.surname} - {c.type}
            </option>
          ))}
        </select>
        <input
          type="number"
          name="currentReading"
          placeholder="Leximi i tanishëm"
          value={form.currentReading}
          onChange={handleChange}
          required
        />
        <button type="submit">Ruaj leximin</button>
      </form>

      <h3 style={{ marginTop: "30px" }}>Historiku i leximeve</h3>
      <ul>
        {readings.map((r) => (
          <li key={r.id}>
            {r.name} {r.surname} | {r.previousReading} → {r.currentReading} m³ | Konsumi: {r.consumption} m³ | Totali: €{r.total.toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
}
